    like: false
  }

  //****************************************The below toggles the state of the card from front faceing to back facing******************* */
  toggleShowingFront = () => {
    this.setState({
      showingFront: !this.state.showingFront
    })
  }



  //**************************************The below toggles the state of the like of the particular card****************************** */

  toggleLike = () => {
      this.setState({
         like: !this.state.like
         }
      )     
  }



