    showingFront : true
  }

  toggleShowingFront = () => {
    this.setState({
      showingFront: !this.state.showingFront
    })
  }

  likeIncreaser = () => {
    likes += 1
    fetch("http://localhost:4000/quotes/1", {
      method: "PATCH",
      headers: {
      "Content-Type": "application/json",
      },
      body: JSON.stringify(likes),
      })
      .then((r) => r.json())
      .then((quoteObj) => console.log(quoteObj));
  }





